//: Playground - noun: a place where people can play

import UIKit

// Day 1
func santaFloorPuzzle(a:String) -> Int {
    var floor = 0
    for c in a.characters {
        switch c {
        case "(".characters.first! :
            floor += 1
        case ")".characters.first!:
            floor -= 1
        default:
            break
        }
    }
    return floor
}

func santaFloorPuzzleMethod2(a:String) -> Int {
    let characters = a.characters
    let upC = "(".characters.first
    let downC = ")".characters.first
    let up = characters.filter({ $0 == upC }).count
    let down = characters.filter({ $0 == downC }).count
    return up - down
}

enum FloorDirection : Character {
    case Up = "("
    case Down = ")"
    case Unknown = "x"
}

extension Int {
    init(floorDirection : FloorDirection) {
        self.init()
        switch floorDirection {
        case .Up:
            self = 1
        case .Down:
            self = -1
        default:
            self = 0
        }
    }
}

func santaFloorPuzzleMethod3(a:String) -> Int {
    return a.characters
        .map({ FloorDirection(rawValue: $0)! })
        .reduce (0, combine: { $0 + Int(floorDirection: $1) })
}

func santaFloorPuzzle2(a:String) throws -> Int {
    var floor = 0
    var index = a.startIndex
    while index < a.endIndex {
        switch a[index] {
        case "(".characters.first! :
            floor += 1
        case ")".characters.first!:
            floor -= 1
        default:
            break
        }
        if floor < 0 {
            break
        }
        index = index.successor()
    }
    if floor < 0 {
        return a.startIndex.distanceTo(index) + 1
    }
    else {
        
    }
    return 0
}

